Bash(sudo bash -c 'cat >> /etc/nginx/sites-available/default << ""EOF""

	# Music directory for Discord bot / Lavalink
	location /music {
		alias /home/mithroll/Shared;

		autoindex on;
		autoindex_exact_size off;
		autoindex_localtime on;

		allow all;

		add_header Access-Control-Allow-Origin *;
		add_header Access-Control-Allow-Methods '"''"'GET, OPTIONS'"''"';
		add_header Access-Control-Allow-Headers '"''"'Range'"''"';

		location ~ \.(mp3|wav|flac|ogg|m4a|aac|opus|webm)$ {
			add_header Cache-Control ""public, max-age=31536000"";
			add_header Access-Control-Allow-Origin *;
		}

		location ~ \.m3u$ {
			add_header Content-Type ""audio/x-mpegurl"";
			add_header Access-Control-Allow-Origin *;
		}
	}
}
EOF
